import logo from './logo.svg';
import './App.css';


const myArr = [
  "first statement",
  "second timestamp",
  "third soco"
]

const Header =(props)=>{
  console.log(props)
  return(
    <header>
      <h1>This is Header {props.name}</h1>
    </header>
  )
}


const Body =(props)=>{
  return(
    <body>
      <h2>This is Body</h2>
      <ul>
        {props.myArrTest.map((item)=>{
          return <li>{item}</li>
          }

        )}
      </ul>      
    </body>
  )
}


const Footer =(props)=>{
  return(
    <footer>
      <h3>This is Footer</h3>
    </footer>
  )
}


function App() {
  return (
    <div className="App">
      <Header name="ABC"/>
      <Body myArrTest={myArr}/>
      <Footer/>
    </div> 
  );
}

export default App;
