import './App.css';


const myArr = [
  "first statement",
  "second timestamp",
  "third soco"
]

const myArrObj = myArr.map((item,index)=>({"id":index,"item":item}))

const Header =(props)=>{
  console.log(props)
  return(
    <header>
      <h1>This is Header {props.name}</h1>
    </header>
  )
}


const Body =(props)=>{
  return(
    <section>
      <h2>This is Body</h2>
      <img src="https://picsum.photos/200/300" height="" alt="it's here"></img>
      <ul style={{textAlign:"left"}}>
        {props.myArrTest.map((data)=> <li key={data.id}>{data.item}</li>)}
      </ul>      
    </section>
  )
}


const Footer =(props)=>{
  return(
    <footer>
      <h3>This is Footer</h3>
    </footer>
  )
}


function App() {
  return (
    <div className="App">
      <Header name="ABC"/>
      <Body myArrTest={myArrObj}/>
      <Footer/>
    </div> 
  );
}

export default App;
